import { json, redirect } from 'react-router-dom';
import AuthForm from '../components/AuthForm';

function AuthenticationPage() {
  return <AuthForm />;
}

export default AuthenticationPage;

export async function action({ request }) {
  const searchParams = new URL(request.url).searchParams;
  const mode = searchParams.get('mode') || 'login';

  // Validate mode
  if (mode !== 'login' && mode !== 'signup') {
    throw json({ message: 'Unsupported mode' }, { status: 422 });
  }

  // Retrieve form data
  const data = await request.formData();
  const authData = {
    email: data.get('email'),
    password: data.get('password'),
  };

  console.log('Auth Data:', authData); // Debugging

  try {
    // Perform the API request
    const response = await fetch(`http://localhost:8080/${mode}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(authData),
    });
    console.log('Auth Data:', authData);

    // Handle specific error responses
    if (response.status === 422 || response.status === 401) {
      return response; // Pass the response back for client-side handling
    }

    // Handle general errors
    if (!response.ok) {
      throw json({ message: 'Could not authenticate user.' }, { status: 500 });
    }

    // Redirect on success
    return redirect('/');
  } catch (error) {
    console.error('Error during authentication:', error); // Debugging
    throw json({ message: 'Authentication failed. Please try again.' }, { status: 500 });
  }
}
